import React, { useState, useEffect } from "react";
import commerce from './lib/Commerce';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import $ from "jquery";

import Index from './components/Index';
import Cart from './components/Cart';

let t_cart = localStorage.getItem("cart");
if (t_cart == null) {
  t_cart = "{}";
} else {
  t_cart = localStorage.getItem("cart");
}

const App = () => {

  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState(t_cart);

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    localStorage.setItem("cart", cart);
    let t_c = JSON.parse(cart);
    for (const prodID in t_c) {
      $("#item-" + prodID).text(JSON.parse(cart)[prodID]);
    }
  }, [cart])

  const addToCart = (productId) => {
    let copyCart = JSON.parse(cart);
    if (!(productId in copyCart)) {
      copyCart[productId] = 0;
    }
    copyCart[productId]++;
    $("#item-" + productId).text(copyCart[productId]);
    setCart(JSON.stringify(copyCart));
  }

  const removeFromCart = (productId) => {
    let copyCart = JSON.parse(cart);
    if (!(productId in copyCart)) {
      copyCart[productId] = 0;
    }
    if (copyCart[productId] > 0) {
      copyCart[productId]--;
    }
    $("#item-" + productId).text(copyCart[productId]);
    setCart(JSON.stringify(copyCart));
  }

  const sendToCart = (productId) => {
    commerce.cart.add(productId, ).then((response) => console.log(response));
  }

  const fetchProducts = () => {
    commerce.products.list().then((products) => {
      setProducts(products.data);
    }).catch((error) => {
      console.log('There was an error fetching the products', error)
    });
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/">
          <Route index element={<Index products={products} addToCart={addToCart} removeFromCart={removeFromCart} cart={JSON.parse(cart)} sendToCart={sendToCart} />} />
          <Route path="cart" element={<Cart />} />
        </Route>
      </Routes>
    </BrowserRouter>
  )
};

export default App;
