import React, { useEffect } from 'react';
import ProductsList from "./ProductsList"
import PropTypes from 'prop-types';

import commerce from './Commerce';

import { useParams } from "react-router-dom";

const Category = ({ addToCart, removeFromCart, sendToCart, loading }) => {

    let params = useParams();
    const [catName, setCatName] = useEffect("");
    const [products, setProducts] = useEffect("[]");
    useEffect(() => {
        commerce.categories.retrieve(params.categorySlug, {type: 'slug'}).then(response => setCatName(response.name));
        commerce.products.list({category_slug: params.categorySlug}).then(response => setProducts(JSON.stringify(response.data)));
    }, []);

    return (
        <div class="d-flex justfiy-content-center content">
            <h5>{catName}</h5>
            <ProductsList
                products={JSON.parse(products)}
                addToCart={addToCart}
                removeFromCart={removeFromCart}
                sendToCart={sendToCart}
                loading={loading}
            />
        </div>
    )
}

Category.propTypes = {
    products: PropTypes.array,
    addToCart: PropTypes.func
};

export default Category;
