import React, { useState, useEffect } from "react";
import commerce from './lib/Commerce';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import $ from "jquery";

// import './styles/scss/styles.scss';

import Index from './components/Index';
import Cart from './components/Cart';

let t_cart = localStorage.getItem("cart");
if (t_cart == null) {
  t_cart = "{}";
} else {
  t_cart = localStorage.getItem("cart");
}

const App = () => {

  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState(t_cart);

  useEffect(() => {
    fetchProducts();
    // alert('hi');
  }, []);

  useEffect(() => {
  //   localStorage.setItem("cart", JSON.stringify(cart));

  //   // alert(localStorage.getItem("cart"))
    $(".items").text("Items: " + cart["prod_aZWNoy3dpEl80J"]);
  }, [cart])

  const addOne = (productId) => {
    let copyCart = JSON.parse(cart);
    if (!(productId in copyCart)) {
      copyCart[productId] = 0;
    }
    copyCart[productId]++;
    setCart(JSON.stringify(copyCart));
    localStorage.setItem("cart", JSON.stringify(copyCart));
  }

  /**
   * Fetch products data from Chec and stores in the products data object.
   * https://commercejs.com/docs/sdk/products
   */
  const fetchProducts = () => {
    commerce.products.list().then((products) => {
      setProducts(products.data);
    }).catch((error) => {
      console.log('There was an error fetching the products', error)
    });
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/">
          <Route index element={<Index products={products} addOne={addOne} cart={cart}/>} />
          <Route path="cart" element={<Cart />} />
        </Route>
      </Routes>
    </BrowserRouter>
  )
};

export default App;
