import React, { useState, useEffect } from "react";
import commerce from './lib/Commerce';
import { BrowserRouter, Routes, Route } from "react-router-dom";

// import './styles/scss/styles.scss';

import Index from './components/Index';
import Cart from './components/Cart';

var t_cart = localStorage.getItem("cart");
if (t_cart == null) {
  t_cart = {};
} else {
  t_cart = JSON.parse(localStorage.getItem("cart"));
}

const App = () => {

  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState(t_cart);

  useEffect(() => {
    fetchProducts();
  }, [products]);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart])

  const addOne = () => {
    setCart({"hi":"bye"});
    alert(localStorage.getItem("cart"));
  }

  /**
   * Fetch products data from Chec and stores in the products data object.
   * https://commercejs.com/docs/sdk/products
   */
  const fetchProducts = () => {
    commerce.products.list().then((products) => {
      setProducts(products.data);
    }).catch((error) => {
      console.log('There was an error fetching the products', error)
    });
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/">
          <Route index element={<Index products={products} addOne={addOne}/>} />
          <Route path="cart" element={<Cart />} />
        </Route>
      </Routes>
    </BrowserRouter>
  )
};

export default App;
