import React, { Component } from "react";
import PropTypes from 'prop-types';
const { stripHtml } = require("string-strip-html");

const ProductItem = ({ key, product, addOne, cart }) => {

  const { result } = stripHtml(product.description);
  const count = cart[product.id];

  return (
    <div class="card product_card">
      <img src={product.image?.url} class="card-img-top" alt={product.name} />
      <div class="card-body">
        <h5 class="card-title">{product.name}</h5>
        <p class="card-text">{result}</p>
        <p class="card-text">{product.price.formatted_with_symbol}</p>
        <p class="card-text">Items: {count}</p>
        <a onClick={() => addOne(key)} class="btn btn-primary">Go somewhere</a>
      </div>
    </div>
  );
};

ProductItem.propTypes = {
  product: PropTypes.object,
};

export default ProductItem;
